import React from 'react'
import Rahul from './Img/Rahul.png'
import top2 from './Img/top2.png'
import top3 from './Img/top3.png'
import top4 from './Img/top4.png'
function AllPlacement() {
    const list = [
        {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            package: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            package: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            package: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            package: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            package: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            package: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            package: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            package: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            package: '40',
            duration: '2015-19'
        }
    ]
    return (
        <>
            <div className='flex flex-col justify-center rounded items-center w-full sm:w-[98%] mx-auto text-center shadow-md my-8'>
                <div className='m-3 block w-full'>
                    <input placeholder='Search' className='float-right p-2 shadow-lg border-gray-700'/>
                </div>
                <div className='flex flex-col p-2 m-2 text-center'>
                    <span className='text-3xl font-sans font-bold'>Placements </span>
                    <span className='text-lg font-mono font-semibold '> (Computer Science and Engineering)</span>
                </div>
                <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 m-2 lg:mx-4 w-full items-center justify-center place-items-center'>
                    {
                        list.map((item, i) => {
                            return (<div key={i} className='w-[85%] sm:w-full m-2  h-[21rem] flex items-center justify-center group'>
                                <div className="flex flex-col items-center justify-center max-w-sm bg-white rounded-lg border-2  h-[19rem] border-gray-200 shadow w-[90%] lg:w-[97%] hover:h-[21rem] hover:w-full hover:absolute">
                                    <div className='text-center font-medium text-xl m-2 p-2 group-hover:text-2xl'>{item.name}</div>
                                    <div className="flex flex-col items-center pb-10">
                                        <img className="m-3 w-24 h-24 rounded-full border-gray-600 shadow-xl group-hover:w-28 group-hover:h-28" src={item.imgurl} alt="..." />
                                        <h4 className="m-1 text-xl font-medium text-gray-900 group-hover:text-2xl">{item.Companyname}</h4>
                                        <h4 className="text-lg text-gray-500 dark:text-gray-400 group-hover:text-xl">{item.package} L.P.A</h4>
                                        <h4 className="text-lg text-gray-500 dark:text-gray-400 group-hover:text-xl">({item.duration})</h4>
                                    </div>
                                </div>
                            </div>)
                        })
                    }
                </div>
            </div>
        </>
    )
}

export default AllPlacement
