import React, { useEffect, useRef } from 'react'
import { useState } from 'react';

function Menu() {
    const [offtop, setOfftop] = useState(false);
    const [width, setWidth] = useState(getWindowSize());
    const myref = useRef();
    const getListsize = () => {
        setWidth(getWindowSize);
    }
    const Handlescroll = () => {
        let offtop1 = myref.current.getBoundingClientRect().top;
        console.log(offtop1)
        if (offtop1 <= 82) {
            if (!offtop)
                setOfftop(true);
        } else {
            setOfftop(false)
        }
    }
    useEffect(() => {
        window.scrollTo(0, 0);
        window.addEventListener('scroll', Handlescroll);
        window.addEventListener('resize', getListsize);
        return () => {
            window.removeEventListener('resize', getListsize);
        };
    }, []);
    return (
        <>
            <div ref={myref} className='bottom-96 border-2 max-w-full w-64 flex flex-col m-2 shadow rounded'>
                <div className='text-lg font-medium p-2 w-full border-b-2 bg-blue-700 text-white rounded-t'>Department of Computer Science and Engineering</div>
                <div className='p-3'>
                    <div className='p-2 shadow hover:bg-blue-500 rounded'><i className='fa fa-home w-10 px-auto'></i>Home</div>
                    <div className='p-2 shadow hover:bg-blue-500 rounded'><i className='fa fa-info w-10 px-auto'></i>About Us</div>
                    <div className='p-2 shadow hover:bg-blue-500 rounded'><i className='fa fa-university w-10 px-auto'></i>Academics</div>
                    <div className='p-2 shadow hover:bg-blue-500 rounded'><i className='fa fa-person w-10 px-auto'></i>People</div>
                    <div className='p-2 shadow hover:bg-blue-500 rounded'><i className='fa fa-flask w-10 px-auto'></i>Research and Labs</div>
                </div>
            </div>
        </>
    )
}

export default Menu

function getWindowSize() {
    const innerwidth = window.innerWidth;
    return innerwidth;
}
