import React, { useEffect, useRef, useState } from 'react'
import Rahul from './Img/Rahul.png'
import top2 from './Img/top2.png'
import top3 from './Img/top3.png'
import top4 from './Img/top4.png'
function AllPlacement({ Handleactivate }) {
    const list = [
        {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }
    ]
    const [search, setSearch] = useState('');
    const [offtop, setOfftop] = useState(true);
    const myref = useRef();
    const Handlescroll = () => {
        let offtop1;
        offtop1 = myref.current.getBoundingClientRect().top;
        if (offtop1 < 1125) {
            if (offtop)
                setOfftop(false);
        } else {
            setOfftop(true)
        }
    }
    const isEqual = (itemE, searchV) => {
        itemE = itemE.toUpperCase();
        searchV = searchV.toUpperCase();
        let value = itemE.indexOf(searchV);
        if (value !== -1) return true;
        else return false;
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        window.addEventListener('scroll', Handlescroll);
    }, [])

    return (
        <>
            <div className='flex flex-col justify-center rounded items-center w-full sm:w-[98%] mx-auto text-center shadow-md my-8'>
                <div className='mx-4 my-2 block w-full'>
                    <button className='flex rounded-full border-2 font-bold shadow-lg p-2 float-left w-8 h-8 items-center justify-center hover:font-extrabold' onClick={Handleactivate}>&larr;</button>
                </div>
                <div className='flex flex-col p-2 m-2 text-center'>
                    <span className='text-3xl font-sans font-bold'>Placements </span>
                    <span className='text-lg font-mono font-semibold '> (Computer Science and Engineering)</span>
                </div>
                <div className='m-3 block w-full'>
                    <input placeholder='Search by (Company Name, Packages or year)' defaultValue={search} onChange={(e) => { setSearch(e.target.value) }} className='w-4/5 md:w-2/3 lg:w-1/3 m-4 p-2 text-lg rounded border-2 shadow-lg border-gray-300 focus:border-gray-400 focus:outline-none' />
                </div>
                <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 2xl:grid-cols-5 m-2 lg:mx-4 w-full items-center justify-center place-items-center'>
                    {
                        list.map((item, i) => {
                            return search ? ((isEqual(item.packages, search) || isEqual(item.duration, search) || isEqual(item.Companyname, search)) ? (
                                <div key={i} ref={myref} className='w-[95%] border-[3px] border-spacing-96 rounded-md bg-opacity-95  h-[13.5rem] m-8 place-items-center shadow-lg'>
                                    <div className='flex items-center w-full text-2xl font-semibold h-12 justify-end pr-12'>{item.Companyname}</div>
                                    <div className='flex items-center border-t-2 border-b-2 justify-between h-28 w-full'>
                                        <div className='h-40 mx-2 border-2 shadow-2xl rounded-full w-40'>
                                            <img src={item.imgurl} alt='...' className='rounded-full w-full h-full overflow-hidden' />
                                        </div>
                                        <div className='mr-8 flex flex-col items-center justify-center'>
                                            <h2 className='text-xl italic font-medium'>{item.name}</h2>
                                            <h4 className='text-lg'>({item.duration})</h4>
                                        </div>
                                    </div>
                                    <div className='flex items-center w-full font-medium text-xl h-12 justify-end pr-4'>Package:- <span className='text-lg ml-2'>40 L.P.A</span></div>
                                </div>
                            ) : <></>) :
                                (
                                    <div key={i} ref={myref} className='w-[95%] max-w-md border-[3px] border-spacing-96 rounded-md bg-opacity-95  h-[13.5rem] m-8 place-items-center shadow-lg'>
                                        <div className='flex items-center w-full text-2xl font-semibold h-12 justify-end pr-12'>{item.Companyname}</div>
                                        <div className='flex items-center border-t-2 border-b-2 justify-between h-28 w-full'>
                                            <div className='h-40 mx-2 border-2 shadow-2xl rounded-full w-40'>
                                                <img src={item.imgurl} alt='...' className='rounded-full w-full h-full overflow-hidden' />
                                            </div>
                                            <div className='mr-8 flex flex-col items-center justify-center'>
                                                <h2 className='text-xl italic font-medium'>{item.name}</h2>
                                                <h4 className='text-lg'>({item.duration})</h4>
                                            </div>
                                        </div>
                                        <div className='flex items-center w-full font-medium text-xl h-12 justify-end pr-4'>Package:- <span className='text-lg ml-2'>40 L.P.A</span></div>
                                    </div>
                                )
                        })
                    }
                </div>
            </div>
        </>
    )
}

export default AllPlacement
