import React, { useEffect, useRef, useState } from 'react'
import Rahul from './Img/Rahul.png'
import top2 from './Img/top2.png'
import top3 from './Img/top3.png'
import top4 from './Img/top4.png'
function AllPlacement() {
    const list = [
        {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Rahul',
            imgurl: Rahul,
            Companyname: 'MicroSoft',
            packages: '43.3',
            duration: '2016-20'
        }, {
            name: 'Taranjeet Kaur',
            imgurl: top2,
            Companyname: 'Adobe',
            packages: '43.17',
            duration: '2016-20'
        }, {
            name: 'Riya Verma',
            imgurl: top3,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }, {
            name: 'Himakshi Salhotra',
            imgurl: top4,
            Companyname: 'Adobe',
            packages: '40',
            duration: '2015-19'
        }
    ]
    const [search, setSearch] = useState('');
    const [offtop, setOfftop] = useState(true);
    const myref = useRef();
    const Handlescroll = () => {
        let offtop1;
        offtop1 = myref.current.getBoundingClientRect().top;
        if (offtop1 < 1125) {
            if (offtop)
                setOfftop(false);
        } else {
            setOfftop(true)
        }
    }
    const isEqual = (itemE, searchV) => {
        itemE = itemE.toUpperCase();
        searchV = searchV.toUpperCase();
        let value = itemE.indexOf(searchV);
        if (value !== -1) return true;
        else return false;
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        window.addEventListener('scroll', Handlescroll);
    }, [])

    return (
        <>
            <div className='overflow-y-scroll overflow-x-hidden w-full bgallplace'>
                <div className='flex flex-col justify-center rounded items-center w-full sm:w-[98%] mx-auto text-center shadow-md'>
                    <div className='flex flex-col p-2 mx-2 my-10 text-center'>
                        <span className='text-4xl font-sans font-bold'>Placements </span>
                        <span className='text-xl font-mono font-semibold '> (Computer Science and Engineering)</span>
                    </div>
                    <div className='m-3 block w-full'>
                        <input placeholder='Search by (Company Name, Packages or year)' defaultValue={search} onChange={(e) => { setSearch(e.target.value) }} className='w-4/5 md:w-2/3 lg:w-1/3 m-4 p-2 text-lg rounded border-2 shadow-lg border-gray-300 focus:border-gray-400 focus:outline-none' />
                    </div>
                    <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 m-2 lg:mx-4 2xl:grid-cols-5 w-full items-center justify-center place-items-center shadow-xl'>
                        {
                            list.map((item, i) => {
                                return search ? ((isEqual(item.packages, search) || isEqual(item.duration, search) || isEqual(item.Companyname, search)) ? (
                                    <div key={i} ref={myref} className='w-[85%] sm:w-full m-2  h-[21rem] flex items-center justify-center group'>
                                        <div className='card'>
                                            <div className='text-center font-medium text-xl p-2 group-hover:text-2xl'>{item.name}</div>
                                            <div className="flex flex-col items-center pb-10">
                                                <img className="m-2 w-28 h-28 rounded-full border-gray-600 shadow-xl group-hover:w-32 group-hover:h-32" src={item.imgurl} alt="..." />
                                                <h4 className="m-1 text-xl font-medium text-gray-900 italic group-hover:text-2xl">{item.Companyname}</h4>
                                                <h4 className="text-lg text-gray-500 dark:text-gray-400 group-hover:text-xl">{item.packages} L.P.A</h4>
                                                <h4 className="text-lg text-gray-500 dark:text-gray-400 group-hover:text-xl">({item.duration})</h4>
                                            </div>
                                        </div>
                                    </div>
                                ) : <></>) :
                                    (
                                        <div key={i} ref={myref} className='w-[85%] sm:w-full m-2  h-[21rem] flex items-center justify-center group'>
                                            <div className='card border-white border rounded-lg shadow-lg'>
                                                <div className='text-center font-medium text-xl p-2 group-hover:text-2xl'>{item.name}</div>
                                                <div className="flex flex-col items-center pb-10">
                                                    <img className="m-2 mb-3 w-28 h-28 rounded-full border-gray-600 shadow-xl group-hover:w-32 group-hover:h-32" src={item.imgurl} alt="..." />
                                                    <h4 className="m-1 text-xl font-medium text-white italic group-hover:text-2xl">{item.Companyname}</h4>
                                                    <h4 className="text-lg text-gray-500 dark:text-gray-400 group-hover:text-xl">{item.packages} L.P.A</h4>
                                                    <h4 className="text-lg text-gray-500 dark:text-gray-400 group-hover:text-xl">({item.duration})</h4>
                                                </div>
                                            </div>
                                        </div>
                                    )
                            })
                        }
                    </div>
                </div>
            </div>
        </>
    )
}

export default AllPlacement
