import React from 'react'
// import { useState } from 'react'

function Programme() {
    // const [Btechopen, setBtechopen] = useState(false)
    // const [Mtechopen, setMtechopen] = useState(false)
    // const [Phdopen, setPhdopen] = useState(false)
    const Programs = [
        {
            Name: 'Btech',
            Syallbus: [
                {sem:'I',link:''},
                {sem:'II',link:''},
                {sem:'III',link:''},
                {sem:'IV',link:''},
                {sem:'V',link:''},
                {sem:'VI',link:''},
                {sem:'VII',link:''},
                {sem:'VIII',link:''},
            ]
        }, {
            Name: 'Mtech',
            Syallbus: [
                {sem:'I',link:''},
                {sem:'II',link:''},
                {sem:'III',link:''},
                {sem:'IV',link:''},
            ]
        }, {
            Name: 'Phd',
            Syallbus: [
                {sem:'I',link:''},
                {sem:'II',link:''},
                {sem:'III',link:''},
                {sem:'IV',link:''},
            ]
        }, {
            Name: 'PG Diploma',
            Syallbus: [
                {sem:'I',link:''},
                {sem:'II',link:''},
                {sem:'III',link:''},
                {sem:'IV',link:''},
            ]
        }
    ]
    return (
        <>
            <div className='w-full h-full flex flex-col flex-auto items-center justify-center p-2'>
                <div className='text-2xl underline underline-offset-8 font-medium p-2 m-2 mb-4 text-center'>Programs of Study</div>
                {
                    Programs.map((item, i) => {
                        return (
                            <div key={i} className='w-full flex justify-between items-center p-2 rounded m-1 text-lg font-medium border-2 border-blue-700 shadow-lg group'>
                                <span>{item.Name}</span>
                                <svg className="text-gray-800" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none"><path d="M15 7.5L10 12.5L5 7.5" stroke="currentColor" strokeWidth="1.75" strokeLinecap="round" stroke-linejoin="round"></path></svg>
                                {/* <svg className="text-gray-800" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" fill="none" strokeLinecap="round" stroke-linejoin="round"><path stroke="none" d="M0 0h24v24H0z" fill="none"></path><polyline points="6 15 12 9 18 15"></polyline></svg> */}
                                <div>
                                    <h1>New Curriculum</h1>
                                    <h2>{item.Name} Syallbus</h2>
                                    <div className='max-h-0 webkit-transition group-hover:max-h-max group-hover:block'>
                                        {
                                            item.Syallbus.map((items,i)=>{
                                                return(
                                                    <a href={items.link}><li className='list-none'>Semester {items.sem}</li></a>
                                                )
                                            })
                                        }
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }

            </div>
        </>
    )
}

export default Programme
