import React from 'react'
import { useState } from 'react'
import sy from '../components/Img/sy.png'
function Syllabus() {
  const List = [
    {
      Program: 'Btech',
      sem: [
        '1st', '2nd', '3rd', '4th', '5th', '6th', '7th', '8th'
      ]
    },
    {
      Program: 'Mtech',
      sem: [
        '1st', '2nd', '3rd', '4th'
      ]
    }
  ]
  const [program, setProgram] = useState('Btech')
  return (
    <>
      <div className='flex flex-col items-center justify-center w-full py-3 bg-emerald-200'>
        <div className='m-2 p-2 shadow-lg'><span className='text-4xl italic'>Syllabus</span></div>
        <div className='w-full p-1 flex items-center justify-center'>
          <select defaultValue={program} onChange={(e) => { setProgram(e.target.value) }} className='border rounded-md focus:border-gray-200 shadow-md w-1/2 p-2'>
            <option value='Btech' selected className='p-2'>Btech</option>
            <option value="Mtech" className='p-2'>Mtech</option>
          </select>
        </div>
        <div className='w-full p-1 flex items-center justify-center'>
          {/* <select className='border rounded-md focus:border-gray-200 shadow-md w-1/2 p-2 '> */}
          {
            List.map((item, i) => {
              return item.Program === program ? (
                <select key={i} className='border rounded-md focus:border-gray-200 shadow-md w-1/3 p-2'>
                  {item.sem.map((item1, j) => {
                    return <option value={j + 1} key={j} ><span className='p-2'>{item1} Sem</span></option>
                  })}
                </select>
              ) : <></>
            })
          }
          {/* </select> */}
        </div>
        <div className='m-3 w-[95%] rounded border shadow bg-slate-50'>
          <div className='flex items-center shadow h-10'>
            <span className='p-2 text-xl font-medium bg-slate-300'>Overview</span>
            <span className='p-2 text-xl font-medium'>Details</span>
          </div>
          <div className='w-full bg-slate-300 p-4 items-center justify-center'>
            <img src={sy} alt='...' className='aspect-video' />
          </div>
        </div>
      </div>
    </>
  )
}

export default Syllabus
