import React, { useEffect, useState } from 'react'
import first from './Img/homepage.jpg'
import sec from './Img/003.jpg'
import third from './Img/02.jpeg'
import four from './Img/002.jpg'
import five from './Img/002.png'
import six from './Img/03.jpeg'
function Test() {
    const Menu = [
        {
            Title: 'About',
            Logo: 'fa fa-info',
            List: [
                {
                    l: 'Vision and Mission'
                }, {
                    l: 'Infrastructure: At a glance'
                }, {
                    l: "HOD's Message"
                }, {
                    l: 'Achievements'
                }, {
                    l: 'Contact Us'
                }
            ],
            open:false
        }, {
            Title: 'Academic',
            Logo: 'fa fa-university',
            List: [
                {
                    l: 'Programmes'
                }, {
                    l: 'Academic Coordinates'
                }, {
                    l: 'Syllabus'
                }, {
                    l: 'Time-Tables'
                }, {
                    l: 'Department Activities Calendar'
                }
            ],
            open:false
        }, {
            Title: 'People',
            Logo: 'fa fa-person',
            List: [
                {
                    l: 'Faculty'
                }, {
                    l: 'PhD Scholars'
                }, {
                    l: 'Students'
                }, {
                    l: 'Alumni*'
                }, {
                    l: 'Staff'
                }
            ],
            open:false
        }, {
            Title: 'Research and Labs',
            Logo: 'fa fa-flask',
            List: [
                {
                    l: 'Research Areas'
                }, {
                    l: 'Department Labs'
                }, {
                    l: 'Publications(Year-Wise)'
                }, {
                    l: 'Projects'
                }, {
                    l: 'Consultancy'
                },{
                    l: 'Research Labs'
                }
            ],
            open:false
        },{
            Title: 'Join Us',
            Logo: 'fa fa-flask',
            List: [
                
            ],
            open:false
        },
    ]
    return (
        <>
            <div className="flex flex-col items-center w-60 h-full overflow-hidden text-gray-700 bg-gray-100 rounded">
                <a className="flex items-center w-full px-3 mt-3 fixed" href="#">
                    <svg className="w-8 h-8 fill-current" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                        <path d="M11 17a1 1 0 001.447.894l4-2A1 1 0 0017 15V9.236a1 1 0 00-1.447-.894l-4 2a1 1 0 00-.553.894V17zM15.211 6.276a1 1 0 000-1.788l-4.764-2.382a1 1 0 00-.894 0L4.789 4.488a1 1 0 000 1.788l4.764 2.382a1 1 0 00.894 0l4.764-2.382zM4.447 8.342A1 1 0 003 9.236V15a1 1 0 00.553.894l4 2A1 1 0 009 17v-5.764a1 1 0 00-.553-.894l-4-2z" />
                    </svg>
                    <span className="ml-2 text-xl font-bold">Menu</span>
                </a>

                <div className="flex flex-col items-center w-full mt-3 border-t border-gray-300">
                    <a className="flex items-center w-full h-12 px-3 mt-2 rounded hover:bg-gray-300" href="#">
                        <svg className="w-6 h-6 stroke-current" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
                        </svg>
                        <span className="ml-2 font-medium">Home</span>
                    </a>
                    {
                        Menu.map((item, i) => {

                            return (
                                <div key={i} className='w-full group'>
                                    <a className="flex items-center w-full h-12 px-3 mt-2 rounded hover:bg-gray-300" href="#">
                                        <i className={ "w-6 h-6 px-auto "+item.Logo}></i>
                                        <span className="ml-2 font-medium">{item.Title}</span>
                                    </a>
                                    <ul id="dropdown-example" className="py-2 space-y-2 hidden group-hover:block transition-all group-hover:transition-all">
                                        {
                                            item.List.map((iteml, j) => {
                                                return (
                                                    <li key={j}>
                                                        <a href="#" className="flex items-center w-full p-2 text-base font-normal text-gray-900 transition duration-75 rounded-lg group hover:bg-gray-200 pl-14">{iteml.l}</a>
                                                    </li>
                                                )
                                            })
                                        }
                                    </ul>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        </>
    )
}

export default Test
