import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import top2 from '../components/Img/top2.png'
import top3 from '../components/Img/top3.png'
import top4 from '../components/Img/top4.png'
function AcadCordinator() {
    const List = [top2, top3, top4, top2, top3, top4]
    const [val, setVal] = useState(0);
    const Handlevalup = () => {
        if (val < List.length - 1)
            setVal(val + 1);
        console.log(val)
    }
    const Handlevaldown = () => {
        if (val > 0)
            setVal(val - 1);
        console.log(val)
    }
    useEffect(() => {
        console.log(val)
    }, [])

    return (
        <>
            <div className='bg-[#231E39] w-full h-[calc(100vh-10rem)] p-4'>
                <div className='bg-[#171326] w-80 max-w-[98%] h-96 border rounded-lg flex flex-col items-center justify-center'>
                    <div className='w-24 h-24 overflow-hidden flex items-center justify-center rounded-full border-2 border-[#7a6caa]'>
                        <img src={top3} alt="..." className='w-full h-full' />
                    </div>
                    <div className='flex flex-col items-center justify-center'>
                        <span className='text-2xl font-bold text-gray-200 '>Riya Verma</span>
                        <span className='text-lg font-medium text-gray-200 py-1'>Assistant Registrar</span>
                        <span className='text-white'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Officia, dolorem.</span>
                    </div>
                </div>
            </div>
        </>
    )
}

export default AcadCordinator
